import{_ as e,c as o,o as s,a as d}from"./app.b66ba44c.js";const S=JSON.parse('{"title":"PostgreSQL\u306E\u6B74\u53F2","description":"","frontmatter":{},"headers":[{"level":2,"title":"postgres","slug":"postgres"},{"level":2,"title":"Postgres95","slug":"postgres95"},{"level":2,"title":"PostgreSQL","slug":"postgresql"}],"relativePath":"db/postgresql/history.md","lastUpdated":1665899290000}'),t={name:"db/postgresql/history.md"},r=d('<h1 id="postgresql\u306E\u6B74\u53F2" tabindex="-1">PostgreSQL\u306E\u6B74\u53F2 <a class="header-anchor" href="#postgresql\u306E\u6B74\u53F2" aria-hidden="true">#</a></h1><h2 id="postgres" tabindex="-1">postgres <a class="header-anchor" href="#postgres" aria-hidden="true">#</a></h2><p>1986\u5E74\u3001<code>postgres</code> \u3068\u3044\u3046\u540D\u524D\u3067\u8A95\u751F\u3057\u307E\u3057\u305F\u3002</p><p>\u3053\u308C\u306F\u3001\u30AB\u30EA\u30D5\u30A9\u30EB\u30CB\u30A2\u5927\u5B66\u30D0\u30FC\u30AF\u30EC\u30A4\u6821\u3067 <code>Michael Stonebraker</code> \u6559\u6388\u3092\u4E2D\u5FC3\u3068\u3057\u3066\u3001\u4E3B\u306B\u7814\u7A76\u76EE\u7684\u3067\u958B\u767A\u3055\u308C\u307E\u3057\u305F\u3002</p><p><code>postgres</code> \u3067\u306F\u3001\u30C7\u30FC\u30BF\u306E\u64CD\u4F5C\u3092 <code>SQL</code> \u8A00\u8A9E\u3067\u306F\u306A\u304F\u3001<code>POSTQUEL</code> \u3068\u3044\u3046\u8A00\u8A9E\u3067\u64CD\u4F5C\u3059\u308B\u3088\u3046\u306B\u306A\u3063\u3066\u3044\u307E\u3059\u3002</p><p>\u305D\u306E\u5F8C\u3001\u7814\u7A76\u3084\u30D3\u30B8\u30CD\u30B9\u306B <code>postgres</code> \u3092\u4F7F\u7528\u3059\u308B\u5834\u9762\u304C\u5897\u3048\u3066\u304D\u307E\u3057\u305F\u304C\u3001\u672C\u6765\u306E\u7814\u7A76\u76EE\u7684\u3067\u306E <code>postgres</code> \u306E\u30E1\u30F3\u30C6\u30CA\u30F3\u30B9\u3084\u30B5\u30DD\u30FC\u30C8\u306A\u3069\u306B\u6642\u9593\u304C\u5897\u3084\u3055\u308C\u3066\u3057\u307E\u3044\u3001<code>postgres</code> \u30D7\u30ED\u30B8\u30A7\u30AF\u30C8\u306F\u30D0\u30FC\u30B8\u30E7\u30F34.2\u3092\u6700\u5F8C\u306B1993\u5E74\u306B\u89E3\u6563\u3068\u306A\u308A\u307E\u3057\u305F\u3002</p><h2 id="postgres95" tabindex="-1">Postgres95 <a class="header-anchor" href="#postgres95" aria-hidden="true">#</a></h2><p><code>postgres</code> \u306F\u3001\u30AB\u30EA\u30D5\u30A9\u30EB\u30CB\u30A2\u5927\u5B66\u30D0\u30FC\u30AF\u30EC\u30A4\u6821\u3067\u306E\u958B\u767A\u7D42\u4E86\u5F8C\u3001<code>Andrew Yu</code> \u3068 <code>Jolly Chen</code> \u3068\u3044\u3046\u5B66\u751F\u306B\u3088\u3063\u3066\u6539\u826F\u3055\u308C\u307E\u3057\u305F\u3002\u3053\u306E\u6539\u826F\u306B\u3088\u3063\u3066\u3001<code>POSTQUEL</code> \u306B\u4EE3\u308F\u3063\u3066 <code>SQL</code> \u8A00\u8A9E\u3092\u4F7F\u3046\u3088\u3046\u306B\u306A\u308A\u307E\u3057\u305F\u3002\u307E\u305F\u3001\u5DE8\u5927\u5316\u3057\u3066\u3044\u305F\u30BD\u30FC\u30B9\u30B3\u30FC\u30C9\u306E\u6574\u7406\u3082\u884C\u308F\u308C\u307E\u3057\u305F\u3002\u6539\u826F\u304C\u7D42\u308F\u3063\u305F <code>postgres</code> \u306F\u30011995\u5E74\u306B Web \u4E0A\u3067 <code>Postgres95</code> \u3068\u3044\u3046\u540D\u524D\u3067\u516C\u958B\u3055\u308C\u307E\u3057\u305F\u3002</p><h2 id="postgresql" tabindex="-1">PostgreSQL <a class="header-anchor" href="#postgresql" aria-hidden="true">#</a></h2><p><code>Andrew Yu</code> \u3068 <code>Jolly Chen</code> \u304C\u5352\u696D\u3057\u305F\u5F8C\u3001\u73FE\u5728\u306E <code>PostgreSQL</code> \u958B\u767A\u30C1\u30FC\u30E0\u3078\u30D7\u30ED\u30B8\u30A7\u30AF\u30C8\u304C\u5F15\u304D\u7D99\u304C\u308C\u307E\u3057\u305F\u3002</p><p>\u307E\u305F\u3001<code>Postgres95</code> \u3068\u3044\u3046\u540D\u524D\u306F1996\u5E74\u4EE5\u964D\u306F\u3075\u3055\u308F\u3057\u304F\u306A\u3044\u3068\u3044\u3046\u3053\u3068\u3067\u3001<code>PostgreSQL</code> \u306B\u5909\u66F4\u3057\u3001\u30D0\u30FC\u30B8\u30E7\u30F3\u30926.0\u3068\u3057\u307E\u3057\u305F\u3002</p><p>\u521D\u671F\u306E <code>PostgreSQL</code> \u3067\u306F\u3001\u65E5\u672C\u8A9E\u306A\u3069\u82F1\u8A9E\u4EE5\u5916\u306E\u8A00\u8A9E\u306B\u306F\u5BFE\u5FDC\u3057\u3066\u3044\u307E\u305B\u3093\u3067\u3057\u305F\u3002\u305D\u3053\u3067\u3001<code>\u77F3\u4E95\u9054\u592B</code>\u6C0F\u304C\u65E5\u672C\u8A9E\u3092\u6271\u3046\u305F\u3081\u306E\u6A5F\u80FD\u3092\u8FFD\u52A0\u3057\u3001\u30D0\u30FC\u30B8\u30E7\u30F36.3\u3067\u305D\u306E\u6A5F\u80FD\u304C\u6B63\u5F0F\u306B\u53D6\u308A\u8FBC\u307E\u308C\u307E\u3057\u305F\u3002\u3053\u306E\u30D0\u30FC\u30B8\u30E7\u30F36.3\u306E\u6BB5\u968E\u3067\u306F\u3001\u65E5\u672C\u8A9E\u5BFE\u5FDC\u306B\u3059\u308B\u305F\u3081\u306B\u30B3\u30F3\u30D1\u30A4\u30EB\u30AA\u30D7\u30B7\u30E7\u30F3\u3092\u4F7F\u7528\u3059\u308B\u5FC5\u8981\u304C\u3042\u308A\u307E\u3057\u305F\u304C\u3001\u30D0\u30FC\u30B8\u30E7\u30F37.3\u304B\u3089\u306F\u6A19\u6E96\u3067\u56FD\u969B\u5316\u5BFE\u5FDC\u306B\u306A\u3063\u3066\u304A\u308A\u3001\u65E5\u672C\u8A9E\u3001\u4E2D\u56FD\u8A9E\u3001\u97D3\u56FD\u8A9E\u306A\u3069\u69D8\u3005\u306A\u8A00\u8A9E\u3092\u6271\u3046\u3053\u3068\u304C\u53EF\u80FD\u306B\u306A\u308A\u307E\u3059\u3002</p><p>\u73FE\u5728\u3067\u306F\u3001\u30A4\u30F3\u30BF\u30CD\u30C3\u30C8\u4E0A\u3067\u4E3B\u306B\u30E1\u30FC\u30EA\u30F3\u30B0\u30EA\u30B9\u30C8\u3092\u4E2D\u5FC3\u3068\u3057\u3066\u30AA\u30FC\u30D7\u30F3\u306B\u958B\u767A\u304C\u884C\u308F\u308C\u3066\u3044\u307E\u3059\u3002\u3044\u308D\u3093\u306A\u6A5F\u80FD\u304C\u3069\u3093\u3069\u3093\u8FFD\u52A0\u3055\u308C\u3001<code>PostgreSQL</code> \u306F\u65E2\u306B\u5546\u7528\u306E\u30C7\u30FC\u30BF\u30D9\u30FC\u30B9\u3068\u6BD4\u3079\u3066\u3082\u905C\u8272\u306E\u306A\u3044\u3082\u306E\u306B\u306A\u3063\u3066\u3044\u307E\u3059\u3002</p>',13),c=[r];function a(p,g,l,h,i,n){return s(),o("div",null,c)}var P=e(t,[["render",a]]);export{S as __pageData,P as default};
